    setConfirmDialogOpen(false)
    setTaskToDelete(null)
  }

  const handleCancelDelete = () => {
    setConfirmDialogOpen(false)
    setTaskToDelete(null)
  }

  const handleReorderTasks = async (taskIds) => {
    const reordered = taskIds.map(id => tasks.find(t => t.id === id))
    setTasks(reordered)
    await reorderTasks(taskIds)
  }

